mapper.select:
SELECT date_format(log_date,'%Y-%m') AS logDate, IFNULL(SUM(send_total),0) AS sendTotal, IFNULL(SUM(report_delivrd),0) AS reportDelivrd, IFNULL(SUM(report_undeliv),0) AS reportUndeliv, IFNULL(SUM(report_unknown),0) AS reportUnknown FROM data_statistic_app_channel WHERE 1 = 1
AND company_id=#{companyId}
GROUP BY date_format(log_date,'%Y-%m')
